import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "./ExpensesFilter";
import "./Expenses.css";
import Card from "../UI/Card";
import { useState } from "react";
function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2021");
  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear); // Two way binding
  };
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          selected={filteredYear} // Set default value as 2021 and pass as prop to child
          onDropdownChange={filterChangeHandler}
        />
        {props.expenses.map((expense) => (
          <ExpenseItem
            key={expense.id} // Have unique ID for items in array
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
        {/*Mapping expenses array objects to the ExpenseItem component for dynamic display */}
      </Card>
    </div>
  );
}
export default Expenses;
